	public int sum(int startNum, int endNum) {
		int sum = 0;
		sum = startNum + endNum;
		return sum;
	}
	
	public int sub(int startNum, int endNum) {
		
		int sum = 0;
		sum = startNum - endNum;
		return sum;
	}
	
	public int multi(int startNum, int endNum) {
		
		int sum = 0;
		sum = startNum * endNum;
		return sum;
	}
	
	public int divi(int startNum, int endNum) {
		
		int sum = 0;
		sum = startNum / endNum;
		return sum;
	}
	
	
	
	
	
	
	
	
